import React, { useEffect, useState } from 'react';
import "./css/Style.css";
const Tempapp = () => {
    const [city, setCity] = useState(null);
    const [search, setSearch] = useState("Bengaluru");
    useEffect(() => {
        const fetchApi = async () => {
            const url = `https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metric&appid=0bca8275c67ddd3113db195d5e4e34c6`;
            const response = await fetch(url)
            const resjson = await response.json()
            // console.log(resjson);
            setCity(resjson.main);
        }
        fetchApi();
    }, [search])
    return (
        <>
            <div className='container'>
                <div className='box'>
                    <div className='inputData'>
                        <input type="search"
                            value={search}
                            className='inputFeild'
                            onChange={(event) => {
                                setSearch(event.target.value)
                            }}
                        />
                    </div>
                    {!city ? (
                        <p>No Data Found</p>
                    ) : (
                        <div>
                            <div className='info'>
                                <h2 className='location'>
                                    <i className="fa-sharp fa-solid fa-street-view"></i>{search}
                                </h2>
                                <h1 className='temp'>{city.temp}°Cel</h1>
                                <h3 className='tempmin_max'>Min:{city.temp_min}°Cel || Max:{city.temp_max}°Cel</h3>
                            </div>
                            <div className='wave1'></div>
                            <div className='wave1'></div>
                            <div className='wave1'></div>
                        </div>
                    )}

                </div>
            </div>
        </>
    )
}

export default Tempapp;
